    private void btnAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddActionPerformed
        // TODO add your handling code here:
        int validador=0;
        for (int i = 0; i < cBx1.getItemCount(); i++) {
            if(cBx1.getItemAt(i).equals(txtNuevaCarrera.getText())){
                validador=1;
            }
        }
        if(validador==1){
            lblCarrera.setText("Carrera ya ingresada!!");
            this.lblPosicion.setText("");
        }else{
            this.cBx1.addItem(this.txtNuevaCarrera.getText());
        }
        this.txtNuevaCarrera.setText("");
        this.txtNuevaCarrera.requestFocus();
        
    }//GEN-LAST:event_btnAddActionPerformed

